CREATE TABLE `#__md_tower` (
  `id` int(3) NOT NULL AUTO_INCREMENT,
  `district_id` int(1) DEFAULT NULL,
  `place` varchar(40) DEFAULT NULL,
  `designation` varchar(40) DEFAULT NULL,
  `bells` int(2) DEFAULT NULL,
  `tenor` varchar(20) DEFAULT NULL,
  `grid_ref` varchar(8) DEFAULT NULL,
  `ground_floor` boolean DEFAULT NULL,
  `anti_clockwise` boolean DEFAULT NULL,
  `unringable` boolean DEFAULT NULL,
  `email` varchar(100) DEFAULT NULL,
  `street` varchar(100) DEFAULT NULL,
  `town` varchar(50) DEFAULT NULL,
  `county` varchar(50) DEFAULT NULL,
  `post_code` varchar(10) DEFAULT NULL,
  `country` varchar(2) DEFAULT NULL,
  `longitude` decimal(35,14) DEFAULT NULL,
  `latitude` decimal(35,14) DEFAULT NULL,
  `website` varchar(200) DEFAULT NULL,
  `church_website` varchar(200) DEFAULT NULL,
  `doves_guide` varchar(200) DEFAULT NULL,
  `contact_person` varchar(25) DEFAULT NULL,
  `email2` int(4) DEFAULT NULL,
  `tower_description` varchar(200) DEFAULT NULL,
  `wc` boolean DEFAULT NULL,
  `sunday_ringing` varchar(100) DEFAULT NULL,
  `active` boolean DEFAULT NULL,
  `web_tower_id` int(3) DEFAULT NULL,
  `multi_towers` boolean DEFAULT NULL,
  `practice_night` varchar(17) DEFAULT NULL,
  `practice_details` varchar(200) DEFAULT NULL,
  `field1` varchar(4) DEFAULT NULL,
  `incl_capt` boolean DEFAULT NULL,
  `incl_corresp` boolean DEFAULT NULL,
  `correspondent_id` int(4) DEFAULT NULL,
  `captain_id` int(5) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;




CREATE TABLE `#__md_member` (
  `id` int(5) NOT NULL AUTO_INCREMENT,
  `tower_id` int(3) DEFAULT NULL,
  `forenames` varchar(50) DEFAULT NULL,
  `surname` varchar(50) DEFAULT NULL,
  `title` varchar(12) DEFAULT NULL,
  `member_type` varchar(13) DEFAULT NULL,
  `insurance_group` varchar(10) DEFAULT NULL,
  `annual_report` boolean DEFAULT NULL,
  `telephone` varchar(28) DEFAULT NULL,
  `email` varchar(100) DEFAULT NULL,
  `newsletters` varchar(7) DEFAULT NULL,
  `date_elected` varchar(21) DEFAULT NULL,
  `address1` varchar(100) DEFAULT NULL,
  `address2` varchar(100) DEFAULT NULL,
  `address3` varchar(100) DEFAULT NULL,
  `town` varchar(50) DEFAULT NULL,
  `county` varchar(20) DEFAULT NULL,
  `postcode` varchar(9) DEFAULT NULL,
  `country` varchar(2) DEFAULT NULL,
  `databaseform` boolean DEFAULT NULL,
  `notes` varchar(200) DEFAULT NULL,
  `crb` boolean DEFAULT NULL,
  `sub_recd` boolean DEFAULT NULL,
  `dbs_date` varchar(9) DEFAULT NULL,
  `dbs_update` varchar(9) DEFAULT NULL,
  PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `#__md_district` (
  `id` int(1) NOT NULL AUTO_INCREMENT,
  `name` varchar(25) DEFAULT NULL,
  `include_in_ar` boolean DEFAULT NULL,
  PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `#__md_tower_verified` ( `id` INT NOT NULL AUTO_INCREMENT , `tower_id` INT NOT NULL , `user_id` INT NOT NULL , `verified_date` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;


